import { img_300 } from "../../config/config";
import { unavailable } from "../../config/config";

const Trendlist = (prop) => {
    const id = prop.id;
    const poster = prop.poster;
    const title = prop.title;
    const date = prop.date;
    const media_type = prop.media_type;
    const vote = prop.vote;

    return (
        <div className=" relative bg-blue-200 shadow-lg xs:w-24  overflow:hidden rounded-lg">
            <img src={poster ? `${img_300}/${poster}` : unavailable} alt={title} className="w-full m-0 rounded-t-lg " />
            <div className="p-2">
                <p>{title}</p>
                <span className="flex justify-between">
                    <p>{media_type === 'tv' ? "TV Series" : "Movie"}</p>
                    <p>{date}</p>
                </span>
            </div>
            <span className="absolute bg-blue-600 text-blue-50 rounded-full top-0 m-1 p-2">{vote}</span>
        </div>
    );
}

export default Trendlist
