import { useEffect } from "react";
import { img_300 } from "../../config/config";
import { unavailable } from "../../config/config";

const Modal = (prop) => {
    const setModal = prop.setModal;
    const type = prop.type;
    const mid = prop.mid;
    const handleClick = () => {
        setModal(false);
    }
    const datareq = async()=>{
        const url = `https://api.themoviedb.org/3/${type}/${mid}?api_key=baa2f39af2ebd5604c32fe6a7b3162e5&language=en-US`;
        const res = await fetch(url);
        const resJson = await res.json();
        console.log(resJson.backdrop_path);
    }

    useEffect(()=>{
        datareq();
    },[])

    return (
        <>
            <div className="bg-zinc-100 opacity-80  fixed inset-0 z-40">
            </div>
            <div className="flex h-screen justify-center items-center fixed inset-0 z-50">
                <div className=" rounded-lg bg-white p-2 opacity-100 ">
                {/* <img src={poster ? `${img_300}/${poster}` : unavailable} alt={title} className="w-full m-0 rounded-t-lg " />
                    <h1></h1>
                    <button onClick={handleClick}> close</button> */}
                </div>
            </div>
        </>
    );
}

export default Modal;
