const Series = () => {
    const [mlist, setMlist] = useState([]);
    const [totalpages, setTotalpages] = useState(1);
    const [page, setPage] = useState(1);
    const movieReq = async () => {
        const url = `https://api.themoviedb.org/3/discover/movie?api_key=baa2f39af2ebd5604c32fe6a7b3162e5&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}&with_watch_monetization_types=flatrate`;

        const res = await fetch(url);
        const resJson = await res.json()
        if (resJson.results) {
            setMlist(resJson.results)
            setTotalpages(resJson.total_pages)
        }
        window.scroll(0, 0);
    }
    
    useEffect(() => {
        movieReq();
    }, [page])

    return ( <>
                    <h1 className="font-bold text-xl p-4 ">TV Series ðŸ“º</h1>
    </> );
}
 
export default Series;
