import React, { useEffect, useState } from 'react';


const Trend = () => {
    const [mlist, setMlist] = useState([]);
    const trendReq = async () => {
        const url = `https://api.themoviedb.org/3/trending/all/week?api_key=baa2f39af2ebd5604c32fe6a7b3162e5`;
        const res = await fetch(url);
        const resJson = await res.json()
        if (resJson.results) {
            setMlist(resJson.results)
        }
    }

    useEffect(() => {
        trendReq();
    }, [])


    return (
        <div>
            <h1 className=' font-bold text-xl justify-center p-4 '>Trending ðŸ”¥</h1>
            <div>
                {
                    mlist && mlist.map((m) => {

                        if (!m.original_title) {
                            <p>{m.original_name}</p>
                        }
                        else {
                            <p>{m.original_title}</p>
                        }
                    })
                }
            </div>
        </div>
    );
}

export default Trend;
