import React, { useEffect, useState } from 'react';
import Serieslist from './partial/serieslist';
import CustomPage from './partial/custompage';

const Series = () => {
    const [slist, setSlist] = useState([]);
    const [totalpages, setTotalpages] = useState(1);
    const [page, setPage] = useState(1);
    const seriesReq = async () => {
        const url = ` https://api.themoviedb.org/3/discover/movie?api_key=baa2f39af2ebd5604c32fe6a7b3162e5&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}&with_watch_monetization_types=flatrate`;


        const res = await fetch(url);
        const resJson = await res.json()
        if (resJson.results) {
            setSlist(resJson.results)
            setTotalpages(resJson.total_pages)
        }
        window.scroll(0, 0);
    }

    useEffect(() => {
        seriesReq();
    }, [page])

    return (
        <>
            <h1 className="font-bold text-xl p-4 ">TV Series ðŸ“º</h1>


            <div className=' gap-8 justify-around grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 p-14 lg:p-20 md:p-12 '>
                {
                    slist && slist.map((m) => <Serieslist
                        key={m.id}
                        id={m.id}
                        poster={m.poster_path}
                        title={m.title || m.name}
                        date={m.release_date || m.first_air_date}
                        vote={m.vote_average}
                    />)
                }
            </div>
            <CustomPage page={page} setPage={setPage} totalpages={totalpages} />
        </>
    );
}

export default Series;
