import { useEffect,useState } from "react";
import { img_500 } from "../../config/config";
import {unavailableLandscape } from "../../config/config";

const Modal = (prop) => {
    const setModal = prop.setModal;
    const type = prop.type;
    const mid = prop.mid;
    const [data,setData] = useState({});
    const [link,setLink] = useState(``);
    const handleClick = () => {
        setModal(false);
    }
    const dataReq = async()=>{
        const url = `https://api.themoviedb.org/3/${type}/${mid}?api_key=baa2f39af2ebd5604c32fe6a7b3162e5&language=en-US`;
        const res = await fetch(url);
        const resJson = await res.json();
        if(resJson){
            setData(resJson);
        }
    }
    const videoReq = async() =>{
        const url = `https://api.themoviedb.org/3/${type}/${mid}/videos?api_key=baa2f39af2ebd5604c32fe6a7b3162e5&language=en-US`;
        
        const res = await fetch(url);
        const resJson = await res.json();
        if(resJson){
            setLink(resJson.results[0].key);
        }
    }
    useEffect(()=>{
        videoReq();
        dataReq();

    },[])
    const poster = data.backdrop_path;
    const title = data.title || data.name;
    const overview = data.overview

    return (
        <>
            <div className="bg-zinc-100 opacity-80  fixed inset-0 z-40">
            </div>
            <div className="flex h-screen justify-center items-center fixed inset-0 z-50">
                <div className=" grid grid-cols-4 rounded-lg bg-white p-2  ">
                <img src={poster ? `${img_500}/${poster}` : unavailableLandscape} alt={title} className=" rounded-lg col-span-2 " />
                <div className=" col-span-2 ml-2">
                    <h1 className="font-bold">{title}</h1>
                    <span className="text-gray-700">{data.tagline}</span>
                    <p className="">{overview}</p>
                    <a className=" bg-black text-white p-2" href={`https://www.youtube.com/watch?v=${link}`} target="_blank">Trailer<svg className="w-6 h-6 inline-block" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd"></path></svg></a>
                    <button onClick={handleClick} className="rounded-full bg-blue-700 text-white px-2 py-1"><svg className="w-6 h-6 inline-block" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"></path></svg></button>
                </div>
                </div>
            </div>
        </>
    );
}

export default Modal;
