import { useEffect } from "react";
import Somegen from "./somegen";

const Genre = (prop) => {
    const type = prop.type;
    const selgen = prop.selgen;
    const setSelgen = prop.setSelgen;
    const gen = prop.gen;
    const setGen = prop.setGen;

    const fetchGen = async () => {

        const url = `https://api.themoviedb.org/3/genre/movie/list?api_key=baa2f39af2ebd5604c32fe6a7b3162e5&language=en-US`;

        const res = await fetch(url);
        const resJson = await res.json()
        setGen(resJson.genres);
    }

    const handleADD = (gn) => {
        setSelgen([...selgen,gn]);
        setGen(gen.filter((g) => g.id !== gen.id));
    }

    useEffect(() => {
        fetchGen();
    }, [])
 
    return (
        <>
            <div className=" m-2 flex flex-wrap justify-between" >
                    {selgen && selgen.map((g) => <Somegen genre={g} selgen={selgen} setSelgen={setSelgen} gen={gen} setGen = {setGen} />)}
                    {gen && gen.map((g) => (
                        <button key = {prop.id} className="m-2 border-2 border-white hover:border-slate-900 px-3 rounded-full bg-blue-200 ${ } " onClick={()=> handleADD(genre)} >
                        {genre.name}
                        </button>
                    ))}
            
            </div>

        </>
    );
}

export default Genre;
